import React, { useState } from 'react';
import { useParams, useLocation, Link } from 'react-router-dom';
import { Layout, Space, DatePicker, Input, InputNumber, Typography, Button, Divider } from 'antd';

const { Text } = Typography;
const { Header, Content, Footer } = Layout;

const ConfirmationScreen = () => {
  const { flightID, selectedSeat } = useParams();
  const location = useLocation();
  const { from, to, selectedSeatPrice, flightPrice } = location.state || {};
  const [totalPrice, setTotalPrice] = useState(0);

  const handlePayment = () => {
    // Calculate total price
    const totalPrice = flightPrice + selectedSeatPrice;
    setTotalPrice(totalPrice);
    // Handle payment logic
  };

  return (
    <Layout>
      <Header style={{ display: 'flex', alignItems: 'center' }}>
        <div className="demo-logo" />
        <h1 style={{ color: 'white', fontSize: '35px' }}>Odeme Ekrani</h1>
      </Header>
      <Content style={{ padding: '0 48px' }}>
        <div style={{ minHeight: 330, padding: 24, borderRadius: '12px' }}>
          <Space direction='horizontal' style={{ gap: '240px' }}>
            <Space direction='vertical' style={{ marginTop: '80px', marginLeft: '100px' }}>
              <Space direction='vertical'>
                <Space>
                  <Input placeholder='Cardholder Name' />
                  <Input placeholder='Cardholder Surname' />
                </Space>
                <Input placeholder='Card Number' />
              </Space>
              <Space style={{ marginLeft: '50px' }}>
                <DatePicker.MonthPicker placeholder='YYYY/MM' />
                <InputNumber placeholder='CVV' min={100} max={999} />
                <Button onClick={handlePayment}>ODE</Button>
              </Space>
            </Space>
            <Space direction='vertical'>
              <Space direction='vertical'>
                <h1 style={{ fontSize: '30px' }}>Toplam: {totalPrice} TL</h1>
                <Divider plain>Hesap</Divider>
                <Text>1 Ucak Bileti: </Text>
                <Text style={{ fontSize: '20px' }}>Nereden: <Text style={{ fontWeight: 'bold', fontSize: '25px' }}>{from}</Text> Nereye: <Text style={{ fontWeight: 'bold', fontSize: '25px' }}>{to}</Text></Text>
                <Text>Secilen Koltuk: {selectedSeat}</Text>
              </Space>
            </Space>
          </Space>
        </div>
      </Content>
      <Footer style={{ textAlign: 'center' }}>
        Ant Design ©{new Date().getFullYear()} Created by Ant UED
      </Footer>
    </Layout>
  );
};

export default ConfirmationScreen;
