    { flightID: 1, seats: 29, availability: 'unavailable', price: 3200 },
    { flightID: 1, seats: 30, availability: 'unavailable', price: 3200 },
  ];

  useEffect(() => {
    const filteredSeats = seatData.filter(seat => seat.flightID.toString() === flightID);
    setSeats(filteredSeats);
  }, [flightID]);

  const handleSeatSelect = (seatNumber) => {
    const updatedSeats = seats.map(seat =>
      seat.seats === seatNumber ? { ...seat, availability: 'unavailable' } : seat
    );
    const selected = updatedSeats.find(seat => seat.seats === seatNumber);
    if (selected && selected.availability === 'unavailable') {
      setSelectedSeat(seatNumber);
      setSelectedSeatPrice(selected.price);
      setSeats(updatedSeats);
    }
  };

  const handleConfirmSelection = () => {
    if (selectedSeat) {
      navigate(`/confirmationscreen/${flightID}/${selectedSeat}`, {
        state: {
          from,
          to,
          selectedSeat,
          selectedSeatPrice,
        },
      });
    } else {
      const availableSeats = seats.filter(seat => seat.availability === 'available');
      const randomSeat = availableSeats[Math.floor(Math.random() * availableSeats.length)];
      navigate(`/confirmationscreen/${flightID}/${randomSeat.seats}`, {
        state: {
          from,
          to,
          selectedSeat: randomSeat.seats,
          selectedSeatPrice: randomSeat.price,
        },
      });
    }
  };

  return (
    <Layout>
      <Header style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <div className="logo" />
        <h1 style={{ color: 'white', fontSize: '24px', margin: 0 }}>Seat Selection: {from} to {to}</h1>
        {/* Add any additional header content here */}
      </Header>
      <Content style={{ padding: '0 50px' }}>
        <div className="site-layout-content">
          <div>
            <div className="seat-grid">
              {seats.map((seat) => (
                <Button
                  key={seat.seats}
                  className={`seat-box ${seat.availability === 'available' ? 'available' : 'unavailable'} ${selectedSeat === seat.seats ? 'selected' : ''}`}
                  onClick={() => handleSeatSelect(seat.seats)}
                  disabled={seat.availability !== 'available'}
                  title={seat.availability === 'available' ? `${seat.price} TL` : 'Unavailable'}
                >
                  {seat.seats}
                </Button>
              ))}
            </div>
            {selectedSeat ? (
              <div style={{ marginTop: '20px' }}>
                <h2>Selected Seat: {selectedSeat}</h2>
                <h2>Price: {selectedSeatPrice} TL</h2>
              </div>
            ) : (
              <h2 style={{ marginTop: '20px' }}>You will be seated randomly at no cost.</h2>
            )}
            <Button type="primary" onClick={handleConfirmSelection} style={{ marginTop: '20px' }}>Confirm Selection</Button>
          </div>
        </div>
      </Content>
      <Footer style={{ textAlign: 'center' }}>Ant Design Â©{new Date().getFullYear()} Created by Ant UED</Footer>
    </Layout>
  );
};

export default SeatSelection;
