import React, { useState } from 'react';
import { Layout, Space, Button, Select, DatePicker, Checkbox } from 'antd';
import { Link } from 'react-router-dom';

const { Header, Content, Footer } = Layout;

const MainPage = () => {
  const [flights, setFlights] = useState([]);
  const [from, setFrom] = useState(null);
  const [to, setTo] = useState(null);
  const [dateRange, setDateRange] = useState(null);
  const [airline, setAirline] = useState(null);

  const veriGetir = async () => {
    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    var requestOptions = {
      method: "GET",
      headers: myHeaders,
      redirect: "follow",
    };

    try {
      const response = await fetch("https://v1.nocodeapi.com/emircankocatepe/google_sheets/JoFdgNIObUDXxQtO?tabId=Sayfa1", requestOptions);

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const result = await response.json();
      if (result.error) {
        throw new Error(`API error: ${result.info}`);
      }

      return result.data;
    } catch (error) {
      console.error('Veri yakalanamadi', error);
      return [];
    }
  };

  const handleSearch = async () => {
    const data = await veriGetir();

    const filteredFlights = data.filter(flight => {
      let match = true;
      if (from && flight.from !== from) match = false;
      if (to && flight.to !== to) match = false;
      if (dateRange) {
        const [start, end] = dateRange;
        if (flight.date < start.format('DD-MM-YYYY') || flight.date > end.format('DD-MM-YYYY')) {
          match = false;
        }
      }
      if (airline && flight.airline !== airline) match = false;
      return match;
    });

    setFlights(filteredFlights);
  };

  return (
    <Layout>
      <Header style={{ display: 'flex', alignItems: 'center' }}>
        <div className="demo-logo" />
        <h1 style={{ color: 'white', fontSize: '35px' }}>myTicket</h1>

      </Header>
      <Content style={{ padding: '0 48px' }}>
        <Space size={12} style={{ gap: '9px' }}>
          <Select
            style={{ width: '200px', height: '40px', marginTop: '20px' }}
            showSearch
            placeholder="From"
            optionFilterProp="label"
            onChange={(value) => setFrom(value)}
            options={[
              { value: 'New York', label: 'New York' },
              { value: 'London', label: 'London' },
              { value: 'Tokyo', label: 'Tokyo' }
            ]}
          />
          <Select
            style={{ width: '200px', height: '40px', marginTop: '20px' }}
            showSearch
            placeholder="To"
            optionFilterProp="label"
            onChange={(value) => setTo(value)}
            options={[
              { value: 'Los Angeles', label: 'Los Angeles' },
              { value: 'Paris', label: 'Paris' },
              { value: 'Sydney', label: 'Sydney' }
            ]}
          />
          <DatePicker.RangePicker
            style={{ height: '40px', marginRight: '50px', marginTop: '20px' }}
            onChange={(dates) => setDateRange(dates)}
          />
          <Select
            style={{ width: '200px', height: '40px', marginTop: '20px' }}
            showSearch
            placeholder="Choose an airline"
            optionFilterProp="label"
            onChange={(value) => setAirline(value)}
            options={[
              { value: 'THY', label: 'THY' },
              { value: 'FRF', label: 'FRF' },
              { value: 'QTR', label: 'QTR' }
            ]}
          />
          <Button
            type="primary"
            style={{ width: '90px', height: '40px', borderRadius: 'round', marginTop: '20px' }}
            onClick={handleSearch}
          >
            Ara
          </Button>
        </Space>
        <br />
        <Space>
          <Checkbox style={{ marginBottom: '15px' }}>Aktarmasiz</Checkbox>
        </Space>
        <div style={{ minHeight: 280, padding: 24 }}>
          {flights.length > 0 ? (
            flights.map(flight => (
              <div key={flight.id} className='showFlight'>
                <p>From: {flight.from}</p>
                <p>To: {flight.to}</p>
                <p>Date: {flight.date}</p>
                <p>Passengers: {flight.passengers}</p>
                <p>Price: ${flight.price}</p>
                <p>Airline: {flight.airline}</p>
                <hr />
                <Link to={`/seatselection`}><Button>Choose</Button></Link>
              </div>
            ))
          ) : (
            <p>No flights found</p>
          )}
        </div>
      </Content>
      <Footer style={{ textAlign: 'center' }}>
        Ant Design Â©{new Date().getFullYear()} Created by Ant UED
      </Footer>
    </Layout>
  );
};

export default MainPage;
