import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { Button, theme } from 'antd';
import './SeatSelection.css';



const SeatSelection = () => {
  const {
    token: { colorBgContainer, borderRadiusLG },
  } = theme.useToken();


  const { flightId } = useParams();
  const [selectedSeat, setSelectedSeat] = useState(null);

  const seats = Array.from({ length: 30 }, (_, index) => index + 1); 

  const handleSeatSelect = (seat) => {
    setSelectedSeat(seat);
  };

  return (
    <div>
      <h1>Seat Selection for Flight {flightId}</h1>
      <div className="seat-grid">
        {seats.map((seat) => (
          <Button
            key={seat}
            className={`seat-box ${selectedSeat === seat ? 'selected' : ''}`}
            onClick={() => handleSeatSelect(seat)}
          >
            {seat}
          </Button>
        ))}
      </div>
      {selectedSeat && (
        <div>
          <h2>You have selected seat {selectedSeat}</h2>
          <Link to={`/confirmationscreen`}> <Button type="primary">Confirm Selection</Button></Link>

        </div>
      )}
    </div>
  );
};

export default SeatSelection;
