import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { Button, Input, Space } from 'antd';
import { useNavigate } from 'react-router-dom';
function Signup(){

        const navigate = useNavigate();
        const [email, setEmail] = useState('');
        const [password, setPassword] = useState('');
        const [error, setError] = useState(null);

        const veriAl = async () => {
          var myHeaders = new Headers();
          myHeaders.append("Content-Type", "application/json");

          var requestOptions = {
            method: "POST",
            headers: myHeaders,
            redirect: "follow",
            body: JSON.stringify([[email,password]])
          };

          try {
            const response = await fetch("https://v1.nocodeapi.com/emircankocatepe/google_sheets/HILItNTkYdoXLExR?tabId=Sayfa1", requestOptions);

            if (!response.ok) {
              throw new Error(`HTTP error! status: ${response.status}`);
            }

            const result = await response.json();
            if (result.error) {
              throw new Error(`API error: ${result.info}`);
            }

            return result.data; 
          } catch (error) {
            console.error('Veri yakalanamadi', error);
            return [];
          }
        };

        const handleRegister = async () => {
          const data = await veriAl();
          data.append(row => row.email === email && row.password === password);

        } 
    return(
        <Space direction='vertical' style={{textAlign:'center'}}>
            <Input   
        type="email"
        placeholder="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        ></Input>
            <Input  
        type="password"
        placeholder="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        ></Input>
        <Input  
        type="password"
        placeholder="password again"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        ></Input>
            <Button onClick={handleRegister}>Register</Button>

        </Space>


    )
}
export default Signup
