import React, { useState } from 'react';
import { Layout, Space, Button, Checkbox, Select, DatePicker, theme, Text } from 'antd';
import './MainPage.css'
import { Link } from 'react-router-dom';
const { Header, Content, Footer } = Layout;

const flights = [
  {
    id: 1,
    from: 'New York',
    to: 'Los Angeles',
    date: '2024-07-01',
    passengers: 2,
    price: 250,
    airline: 'THY'

  },
  {
    id: 2,
    from: 'London',
    to: 'Paris',
    date: '2024-07-05',
    passengers: 1,
    price: 180,
    airline: 'FRF'
  },
  {
    id: 3,
    from: 'Tokyo',
    to: 'Sydney',
    date: '2024-07-10',
    passengers: 3,
    price: 500,
    airline: 'QTR'
  }
];

const filterFlights = (from, to, dateRange, airline) => {
  return flights.filter(flight => {
    let match = true;
    if (from && flight.from !== from) match = false;
    if (to && flight.to !== to) match = false;
    if (dateRange) {
      const [start, end] = dateRange;
      if (flight.date < start.format('YYYY-MM-DD') || flight.date > end.format('YYYY-MM-DD')) {
        match = false;
      }
    }
    if (airline && flight.airline !== airline) match = false;
    return match;
  });
};
const MainPage = () => {
    const {
        token: { colorBgContainer, borderRadiusLG },
      } = theme.useToken();

      const [from, setFrom] = useState(null);
      const [to, setTo] = useState(null);
      const [dateRange, setDateRange] = useState(null);
      const [airline, setAirline] = useState(null);
      const [filteredResults, setFilteredResults] = useState([]);

      const handleSearch = () => {
        const results = filterFlights(from, to, dateRange, airline);
        setFilteredResults(results);
      };

      return (
        <Layout>
          <Header
            style={{
              display: 'flex',
              alignItems: 'center',
            }}
          >
            <div className="demo-logo" />
            <h1 style={{ color: 'white', fontSize: '35px' }}>myTicket</h1>
          </Header>
          <Content
            style={{
              padding: '0 48px',
            }}
          >
            <Space size={12} style={{ gap: '9px' }}>
              <Select
                style={{ width: '200px', height: '40px', marginTop: '20px' }}
                showSearch
                placeholder="From"
                optionFilterProp="label"
                onChange={(value) => setFrom(value)}
                options={[
                  { value: 'New York', label: 'New York' },
                  { value: 'London', label: 'London' },
                  { value: 'Tokyo', label: 'Tokyo' }
                ]}
              />
              <Select
                style={{ width: '200px', height: '40px', marginTop: '20px'  }}
                showSearch
                placeholder="To"
                optionFilterProp="label"
                onChange={(value) => setTo(value)}
                options={[
                  { value: 'Los Angeles', label: 'Los Angeles' },
                  { value: 'Paris', label: 'Paris' },
                  { value: 'Sydney', label: 'Sydney' }
                ]}
              />
              <DatePicker.RangePicker
                style={{ height: '40px', marginRight: '50px', marginTop: '20px'  }}
                onChange={(dates) => setDateRange(dates)}
              />
              <Select
                style={{ width: '200px', height: '40px', marginTop: '20px' }}
                showSearch
                placeholder="Choose an airline"
                optionFilterProp="label"
                onChange={(value) => setAirline(value)}
                options={[
                  { value: 'THY', label: 'THY' },
                  { value: 'FRF', label: 'FRF' },
                  { value: 'QTR', label: 'QTR' }
                ]}
              />
              <Button
                type="primary"
                style={{ width: '90px', height: '40px', borderRadius: 'round', marginTop: '20px'  }}
                onClick={handleSearch}
              >
                Ara
              </Button>
            </Space>
            <br />
            <Space>
              <Checkbox style={{ marginBottom: '15px' }}>Aktarmasiz</Checkbox>
            </Space>
            <div
              style={{
                background: colorBgContainer,
                minHeight: 280,
                padding: 24,
                borderRadius: borderRadiusLG,
              }}
            >
              {filteredResults.length > 0 ? (
                filteredResults.map(flight => (
                  <div key={flight.id} className='showFlight'>
                    <p>From: {flight.from}</p>
                    <p>To: {flight.to}</p>
                    <p>Date: {flight.date}</p>
                    <p>Passengers: {flight.passengers}</p>
                    <p>Price: ${flight.price}</p>
                    <p>Airline: {flight.airline}</p>
                    <hr />
                    <Link to={`/seatselection`}><Button>Choose</Button></Link>
                  </div>
                ))
              ) : (
                <p>No flights found</p>
              )}
            </div>
          </Content>
          <Footer
            style={{
              textAlign: 'center',
            }}
          >
            Ant Design Â©{new Date().getFullYear()} Created by Ant UED
          </Footer>
        </Layout>
      );
    };
export default MainPage
