import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { Button, Input, Space } from 'antd';
function Login(){
    const Login = () => {
        const navigate = useNavigate();
        const [email, setEmail] = useState('');
        const [password, setPassword] = useState('');
        const [error, setError] = useState(null);

        const fetchDataFromGoogleSheets = async () => {
          var myHeaders = new Headers();
          myHeaders.append("Content-Type", "application/json");

          var requestOptions = {
            method: "GET",
            headers: myHeaders,
            redirect: "follow",
          };

          try {
            const response = await fetch("https://v1.nocodeapi.com/emircankocatepe/google_sheets/HILItNTkYdoXLExR?tabId=Sayfa1", requestOptions);

            if (!response.ok) {
              throw new Error(`HTTP error! status: ${response.status}`);
            }

            const result = await response.json();
            if (result.error) {
              throw new Error(`API error: ${result.info}`);
            }

            return result.data; // Assuming the data is in `result.data`
          } catch (error) {
            console.error('Error fetching data from Google Sheets:', error);
            return [];
          }
        };

        const handleLogin = async () => {
          const data = await fetchDataFromGoogleSheets();
          const user = data.find(row => row.email === email && row.password === password);

          if (user) {
            // Successful login
            console.log('Login successful:', user);
            navigate('/mainpage'); // Navigate to the main page after login
          } else {
            // Login failed
            setError('Invalid email or password');
          }
        };
    return(
        <Space>
            <Input placeholder="email" type="email"></Input>
            <Input placeholder="password" type="password"></Input>
            <Link to="/mainpage"><Button>Login</Button></Link>

        </Space>


    )
}}
export default Login
