import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { Button, Input, Space } from 'antd';
import { useNavigate } from 'react-router-dom';
function Login(){

        const navigate = useNavigate();
        const [email, setEmail] = useState('');
        const [password, setPassword] = useState('');
        const [error, setError] = useState(null);

        const veriGetir = async () => {
          var myHeaders = new Headers();
          myHeaders.append("Content-Type", "application/json");

          var requestOptions = {
            method: "GET",
            headers: myHeaders,
            redirect: "follow",
          };

          try {
            const response = await fetch("https://v1.nocodeapi.com/emircankocatepe/google_sheets/HILItNTkYdoXLExR?tabId=Sayfa1", requestOptions);

            if (!response.ok) {
              throw new Error(`HTTP error! status: ${response.status}`);
            }

            const result = await response.json();
            if (result.error) {
              throw new Error(`API error: ${result.info}`);
            }

            return result.data; 
          } catch (error) {
            console.error('Veri yakalanamadi', error);
            return [];
          }
        };

        const handleLogin = async () => {
          const data = await veriGetir();
          const user = data.find(row => row.email === email && row.password === password);

          if (user) {

            console.log('Giris basaili:', user);
            navigate('/mainpage'); 
          } else {

            setError('Gecersiz e-posta veya sifre');
          }
        } 
    return(
        <Space direction='vertical' style={{margin: '200px 300px'}}>
            <Input   
        type="email"
        placeholder="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        ></Input>
            <Input  
        type="password"
        placeholder="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        ></Input>
            <Button onClick={handleLogin}>Login</Button>

        </Space>


    )
}
export default Login
