import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { Button } from 'antd';
import './SeatSelection.css';

const SeatSelection = () => {
  const { flightID } = useParams();
  const [seats, setSeats] = useState([]);
  const [selectedSeat, setSelectedSeat] = useState(null);

  useEffect(() => {
    const fetchSeatData = async () => {
      var myHeaders = new Headers();
      myHeaders.append("Content-Type", "application/json");

      var requestOptions = {
        method: "GET",
        headers: myHeaders,
        redirect: "follow",
      };

      try {
        const response = await fetch("https://v1.nocodeapi.com/emircankocatepe/google_sheets/JoFdgNIObUDXxQtO?tabId=Sayfa2", requestOptions);

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const result = await response.json();
        if (result.error) {
          throw new Error(`API error: ${result.info}`);
        }

        const seatData = result.data.filter(seat => seat.flightID === flightID);
        setSeats(seatData);
      } catch (error) {
        console.error('Veri yakalanamadi', error);
      }
    };

    fetchSeatData();
  }, [flightID]);

  const handleSeatSelect = (seatNumber) => {
    const selected = seats.find(seat => seat.seats === seatNumber.toString());
    if (selected && selected.availability === 'available') {
      setSelectedSeat(seatNumber);
    }
  };


  return (
    <div>
      <h1>Seat Selection for Flight: {flightID}</h1>
      <div className="seat-grid">
        {seats.map((seat) => (
          <Button
            key={seat.seats}
            className={`seat-box ${seat.availability === 'available' ? 'available' : 'unavailable'} ${selectedSeat === parseInt(seat.seats) ? 'selected' : ''}`}
            onClick={() => handleSeatSelect(parseInt(seat.seats))}
            disabled={seat.availability !== 'available'}
          >
            {seat.seats}
          </Button>
        ))}
      </div>
      {selectedSeat && (
        <div>
          <h2>Secilen Koltuk: {selectedSeat}</h2>
          <Link to={`/confirmationscreen`}>
            <Button type="primary" >Confirm Selection</Button>
          </Link>
        </div>
      )}
    </div>
  );
};

export default SeatSelection;
