import React from 'react';
import { Layout, Space, DatePicker, Input, InputNumber, Typography } from 'antd';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import './ConfirmationScreen.css';

const { Text } = Typography;
const { Header, Content, Footer } = Layout;

const ConfirmationScreen = () => {
  const { flightID, selectedSeat} = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const { from, to} = location.state || {};



  return (
    <Layout>
      <Header style={{ display: 'flex', alignItems: 'center' }}>
        <div className="demo-logo" />
        <h1 style={{ color: 'white', fontSize: '35px' }}>Payment</h1>
      </Header>
      <Content style={{ padding: '0 48px' }}>
        <div style={{ background: '#ffffff', minHeight: 330, padding: 24, borderRadius: 8 }}>
          <Space direction='horizontal' style={{ gap: '240px' }}>
            <Space direction='vertical' style={{ marginTop: '80px', marginLeft: '100px' }}>
              <Space direction='vertical'>
                <Space>
                  <Input placeholder='Cardholder Name'></Input>
                  <Input placeholder='Cardholder Surname'></Input>
                </Space>
                <Input placeholder='Card Number'></Input>
              </Space>
              <Space style={{ marginLeft: '50px' }}>
                <DatePicker.MonthPicker placeholder='YYYY/MM'></DatePicker.MonthPicker>
                <InputNumber placeholder='CVV' min={100} max={999}></InputNumber>
              </Space>
            </Space>
            <Space direction='vertical'>
              <Space direction='vertical'>

                <Text>1 Flight Ticket: {from} - {to}</Text>
                <Text>Selected Seat: {selectedSeat}</Text>
              </Space>
            </Space>
          </Space>
        </div>
      </Content>
      <Footer style={{ textAlign: 'center' }}>
        Ant Design Â©{new Date().getFullYear()} Created by Ant UED
      </Footer>
    </Layout>
  );
};

export default ConfirmationScreen;
