  };

  return (
    <BrowserRouter>
      <Routes>
        <Route path='/' element={<Login onLoginSuccess={handleLoginSuccess} />} />
        <Route 
          path="/mainpage" 
          element={
            <DogrulamaYonu isDogrulandi={isDogrulandi}>
              <MainPage />
            </DogrulamaYonu>
