          element={
            <DogrulamaYonu isDogrulandi={isDogrulandi}>
              <SeatSelection/>
            </DogrulamaYonu>
          }
        />
        <Route 
          path="/confirmationscreen" 
          element={
            <DogrulamaYonu isDogrulandi={isDogrulandi}>
              <ConfirmationScreen />
            </DogrulamaYonu>
          }
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
