import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Button } from 'antd';
import './SeatSelection.css';

const SeatSelection = () => {
  const { flightID, from, to } = useParams();
  const [seats, setSeats] = useState([]);
  const [selectedSeat, setSelectedSeat] = useState(null);
  const navigate = useNavigate();

  const seatData = [
    { flightID: 4, seats: 1, availability: 'available', price: 3200 },
    // ...other seat data
  ];

  useEffect(() => {
    const filteredSeats = seatData.filter(seat => seat.flightID.toString() === flightID);
    setSeats(filteredSeats);
  }, [flightID]);

  const handleSeatSelect = (seatNumber) => {
    const selected = seats.find(seat => seat.seats === seatNumber.toString());
    if (selected && selected.availability === 'available') {
      setSelectedSeat(seatNumber);
    }
  };

  const handleConfirmSelection = () => {
    const selectedSeatData = seats.find(seat => seat.seats === selectedSeat.toString());
    navigate(`/confirmationscreen/${flightID}/${selectedSeat}`, {
      state: {
        from,
        to,
        price: selectedSeatData ? selectedSeatData.price : 0,
      },
    });
  };

  return (
    <div>
      <h1>Seat Selection for Flight: {from} - {to}</h1>
      <div className="seat-grid">
        {seats.map((seat) => (
          <Button
            key={seat.seats}
            className={`seat-box ${seat.availability === 'available' ? 'available' : 'unavailable'} ${selectedSeat === parseInt(seat.seats) ? 'selected' : ''}`}
            onClick={() => handleSeatSelect(parseInt(seat.seats))}
            disabled={seat.availability !== 'available'}
          >
            {seat.seats}
          </Button>
        ))}
      </div>
      {selectedSeat && (
        <div>
          <h2>Selected Seat: {selectedSeat}</h2>
          <Button type="primary" onClick={handleConfirmSelection}>Confirm Selection</Button>
        </div>
      )}
    </div>
  );
};

export default SeatSelection;
