import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { Button } from 'antd';
import './SeatSelection.css';

const SeatSelection = () => {
  const { flightID } = useParams();
  const [seats, setSeats] = useState([]);
  const [selectedSeat, setSelectedSeat] = useState(null);

  useEffect(() => {
    const fetchSeats = async () => {
      var myHeaders = new Headers();
      myHeaders.append("Content-Type", "application/json");

      var requestOptions = {
        method: "GET",
        headers: myHeaders,
        redirect: "follow",
      };

      try {
        const response = await fetch("https://v1.nocodeapi.com/emircankocatepe/google_sheets/JoFdgNIObUDXxQtO?tabId=Sayfa2", requestOptions);

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const result = await response.json();
        if (result.error) {
          throw new Error(`API error: ${result.info}`);
        }

        const filteredSeats = result.data.filter(seat => seat.flightID === flightID);
        setSeats(filteredSeats);
      } catch (error) {
        console.error('Error fetching seats:', error);
      }
    };

    fetchSeats();
  }, [flightID]);

  const handleSeatSelect = (seat) => {
    setSelectedSeat(seat);
  };

  return (
    <div>
      <h1>Seat Selection for Flight: {flightID}</h1>
      <div className="seat-grid">
        {seats.map((seat) => (
          <Button
            key={seat.seats}
            className={`seat-box ${seat.availability === 'unavailable' ? 'unavailable' : ''} ${selectedSeat === seat.seats ? 'selected' : ''}`}
            onClick={() => seat.availability === 'available' && handleSeatSelect(seat.seats)}
          >
            {seat.seats}
          </Button>
        ))}
      </div>
      {selectedSeat && (
        <div>
          <h2>You have selected seat {selectedSeat}</h2>
          <Link to={`/confirmationscreen/${flightID}/${selectedSeat}`}>
            <Button type="primary">Confirm Selection</Button>
          </Link>
        </div>
      )}
    </div>
  );
};

export default SeatSelection;
