import React, { useState } from 'react';
import { Layout, Space, Button, Select, DatePicker, Checkbox } from 'antd';
import { Link, useNavigate } from 'react-router-dom';

const { Header, Content, Footer } = Layout;

const MainPage = () => {
  const [flights, setFlights] = useState([]); // Your flights state if fetched from an API
  const [from, setFrom] = useState(null);
  const [to, setTo] = useState(null);
  const [dateRange, setDateRange] = useState(null);
  const [airline, setAirline] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isDirect, setIsDirect] = useState(false);
  const [sortBy, setSortBy] = useState(null);
  const navigate = useNavigate();

  // Example flight data (replace with actual data fetching logic)
  const flightData = [
    { flightID: 1, from: 'New York', to: 'Paris', date: '24-07-2024', airline: 'Turkish Airlines', price: 12000, aktarma: 'aktarmali', sure: 10 },
    { flightID: 2, from: 'Tokyo', to: 'Istanbul', date: '11-09-2024', airline: 'Qatar Airlines', price: 25000, aktarma: 'direkt', sure: 13 },
    // Add more flights as needed
  ];

  // Handle search and filter flights based on user input
  const handleSearch = () => {
    setIsLoading(true);

    // Example filter logic (replace with actual filtering based on your data)
    let filteredFlights = flightData;

    if (from) {
      filteredFlights = filteredFlights.filter(flight => flight.from === from);
    }
    if (to) {
      filteredFlights = filteredFlights.filter(flight => flight.to === to);
    }
    if (dateRange) {
      const [start, end] = dateRange;
      filteredFlights = filteredFlights.filter(flight => flight.date >= start.format('DD-MM-YYYY') && flight.date <= end.format('DD-MM-YYYY'));
    }
    if (airline) {
      filteredFlights = filteredFlights.filter(flight => flight.airline === airline);
    }

    setFlights(filteredFlights);
    setIsLoading(false);
  };

  // Handle checkbox for direct flights filter
  const handleDirect = (e) => {
    setIsDirect(e.target.checked);
  };

  // Handle sorting of flights by price or duration
  const handleSort = (value) => {
    setSortBy(value);
  };

  // Handle logout action
  const handleLogout = () => {
    navigate('/');
  };

  return (
    <Layout>
      <Header style={{ display: 'flex', alignItems: 'center' }}>
        <div className="demo-logo" />
        <h1 style={{ color: 'white', fontSize: '35px' }}>myTicket</h1>
        <Button onClick={handleLogout}>Logout</Button>
      </Header>
      <Content style={{ padding: '0 48px' }}>
        <Space size={12} style={{ gap: '9px' }}>
          <Select
            style={{ width: '200px', height: '40px', marginTop: '20px' }}
            showSearch
            placeholder="From"
            optionFilterProp="label"
            onChange={(value) => setFrom(value)}
            // Options array (example, replace with your options)
            options={[
              { value: 'New York', label: 'New York' },
              { value: 'London', label: 'London' },
              // Add more options as needed
            ]}
          />
          <Select
            style={{ width: '200px', height: '40px', marginTop: '20px' }}
            showSearch
            placeholder="To"
            optionFilterProp="label"
            onChange={(value) => setTo(value)}
            // Options array (example, replace with your options)
            options={[
              { value: 'Istanbul', label: 'Istanbul' },
              { value: 'Paris', label: 'Paris' },
              // Add more options as needed
            ]}
          />
          <DatePicker.RangePicker
            style={{ height: '40px', marginRight: '50px', marginTop: '20px' }}
            onChange={(dates) => setDateRange(dates)}
          />
          <Select
            style={{ width: '200px', height: '40px', marginTop: '20px' }}
            showSearch
            placeholder="Choose an airline"
            optionFilterProp="label"
            onChange={(value) => setAirline(value)}
            // Options array (example, replace with your options)
            options={[
              { value: 'Turkish Airlines', label: 'Turkish Airlines' },
              { value: 'Qatar Airlines', label: 'Qatar Airlines' },
              // Add more options as needed
            ]}
          />
          <Button
            type="primary"
            style={{ width: '90px', height: '40px', borderRadius: 'round', marginTop: '20px' }}
            onClick={handleSearch}
          >
            Ara
          </Button>
        </Space>
        <br />
        <Space>
          <Checkbox
            style={{ marginBottom: '15px' }}
            checked={isDirect}
            onChange={handleDirect}
          >
            Direkt Ucuslar
          </Checkbox>
          <Select
            placeholder='Ucusu Filtrele'
            style={{ width: '200px', height: '40px', marginTop: '20px' }}
            onChange={handleSort}
            options={[
              { value: 'price', label: 'Once en ucuz' },
              { value: 'duration', label: 'Once en kisa' },
            ]}
          />
        </Space>
        <div style={{ minHeight: 280, padding: 24 }}>
          {isLoading ? (
            <p>Yukleniyor...</p>
          ) : (
            flights.length > 0 ? (
              flights.map(flight => (
                <div key={flight.flightID} className='showFlight'>
                  <p>From: {flight.from}</p>
                  <p>To: {flight.to}</p>
                  <p>Date: {flight.date}</p>
                  <p>Passengers: {flight.passengers}</p>
                  <p>Yolculuk Suresi: {flight.sure} saat</p>
                  <p>Aktarma: {flight.aktarma}</p>
                  <p>Price: {flight.price} TL</p>
                  <p>Airline: {flight.airline}</p>
                  <p>ID: {flight.flightID}</p>
                  <hr />
                  <Link to={{
                    pathname: `/seatselection/${flight.flightID}/${flight.from}/${flight.to}`,
                    state: { flightPrice: flight.price } // Pass flight price to SeatSelection
                  }}>
                    <Button>Choose</Button>
                  </Link>
                </div>
              ))
            ) : (
              <p>Ucuslari gormek icin arama yapin.</p>
            )
          )}
        </div>
      </Content>
      <Footer style={{ textAlign: 'center' }}>
        Ant Design Â©{new Date().getFullYear()} Created by Ant UED
      </Footer>
    </Layout>
  );
};

export default MainPage;
