import React, { useState, useEffect } from 'react';
import { Layout, Space, Button, Select, DatePicker, Checkbox } from 'antd';
import { Link, useNavigate } from 'react-router-dom';

const { Header, Content, Footer } = Layout;

const MainPage = () => {
  const [flights, setFlights] = useState([]);
  const [from, setFrom] = useState(null);
  const [to, setTo] = useState(null);
  const [dateRange, setDateRange] = useState(null);
  const [airline, setAirline] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isDirect, setIsDirect] = useState(false);
  const [sortBy, setSortBy] = useState(null);
  const [initialFlights, setInitialFlights] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    setIsLoading(true);
    fetch("https://v1.nocodeapi.com/emircan/google_sheets/TXdflyxmbKczXbgd?tabId=Sayfa1")
      .then(response => response.json())
      .then(result => {
        setInitialFlights(result.data);
        setFlights(result.data); // Initialize flights state with all data
        setIsLoading(false);
      })
      .catch(error => {
        console.error('Error fetching flight data:', error);
        setIsLoading(false);
      });
  }, []);

  const handleSearch = () => {
    setIsLoading(true);

    let filteredFlights = initialFlights;

    if (from) {
      filteredFlights = filteredFlights.filter(flight => flight.from === from);
    }
    if (to) {
      filteredFlights = filteredFlights.filter(flight => flight.to === to);
    }
    if (dateRange) {
      const [start, end] = dateRange;
      filteredFlights = filteredFlights.filter(flight => flight.date >= start.format('DD-MM-YYYY') && flight.date <= end.format('DD-MM-YYYY'));
    }
    if (airline) {
      filteredFlights = filteredFlights.filter(flight => flight.airline === airline);
    }

    if (isDirect) {
      filteredFlights = filteredFlights.filter(flight => flight.aktarma === 'Direkt');
    }

    setFlights(filteredFlights);
    setIsLoading(false);
  };

  useEffect(() => {
    let updatedFlights = [...initialFlights];

    if (isDirect) {
      updatedFlights = updatedFlights.filter(flight => flight.aktarma === 'Direkt');
    }

    if (sortBy === 'price') {
      updatedFlights.sort((a, b) => a.price - b.price);
    } else if (sortBy === 'duration') {
      updatedFlights.sort((a, b) => a.sure - b.sure);
    }

    setFlights(updatedFlights);
  }, [isDirect, sortBy, initialFlights]);

  const handleDirect = (e) => {
    setIsDirect(e.target.checked);
  };

  const handleSort = (value) => {
    setSortBy(value);
  };

  const handleLogout = () => {
    navigate('/');
  };

  return (
    <Layout>
      <Header style={{ display: 'flex', alignItems: 'center' }}>
        <div className="demo-logo" />
        <h1 style={{ color: 'white', fontSize: '35px' }}>myTicket</h1>
        <Button onClick={handleLogout}>Logout</Button>
      </Header>
      <Content style={{ padding: '0 48px' }}>
        <Space size={12}>
          <Select
            style={{ width: '200px', marginTop: '20px' }}
            showSearch
            placeholder="From"
            onChange={(value) => setFrom(value)}
            options={[
              { value: 'New York', label: 'New York' },
              { value: 'London', label: 'London' },
              { value: 'Toronto', label: 'Toronto' },
              { value: 'Doha', label: 'Doha' },
              { value: 'Sevilla', label: 'Sevilla' },
              { value: 'Moskova', label: 'Moskova' },
              { value: 'Tokyo', label: 'Tokyo' },
            ]}
          />
          <Select
            style={{ width: '200px', marginTop: '20px' }}
            showSearch
            placeholder="To"
            onChange={(value) => setTo(value)}
            options={[
              { value: 'Istanbul', label: 'Istanbul' },
              { value: 'Paris', label: 'Paris' },
              { value: 'Barcelona', label: 'Barcelona' },
              { value: 'Prag', label: 'Prag' },
              { value: 'Amsterdam', label: 'Amsterdam' },
              { value: 'Tel Aviv', label: 'Tel Aviv' },
              { value: 'Kopenhag', label: 'Kopenhag' },
              { value: 'Sofia', label: 'Sofia' },
            ]}
          />
          <DatePicker.RangePicker
            style={{ marginRight: '50px', marginTop: '20px' }}
            onChange={(dates) => setDateRange(dates)}
          />
          <Select
            style={{ width: '200px', marginTop: '20px' }}
            showSearch
            placeholder="Choose an airline"
            onChange={(value) => setAirline(value)}
            options={[
              { value: 'Turkish Airlines', label: 'Turkish Airlines' },
              { value: 'Qatar Airlines', label: 'Qatar Airlines' },
              { value: 'Delta Airlines', label: 'Delta Airlines' },
              { value: 'Air Canada', label: 'Air Canada' },
              { value: 'United Airlines', label: 'United Airlines' },
            ]}
          />
          <Button
            type="primary"
            style={{ width: '90px', borderRadius: 'round', marginTop: '20px' }}
            onClick={handleSearch}
          >
            Ara
          </Button>
        </Space>
        <Space>
          <Checkbox
            style={{ marginBottom: '15px' }}
            checked={isDirect}
            onChange={handleDirect}
          >
            Direkt Ucuslar
          </Checkbox>
          <Select
            placeholder='Ucusu Filtrele'
            style={{ width: '200px', marginTop: '20px' }}
            onChange={handleSort}
            options={[
              { value: 'price', label: 'Once en ucuz' },
              { value: 'duration', label: 'Once en kisa' },
            ]}
          />
        </Space>
        <div style={{ minHeight: 280, padding: 24 }}>
          {isLoading ? (
            <p>Yukleniyor...</p>
          ) : (
            flights.length > 0 ? (
              flights.map(flight => (
                <div key={flight.flightID}>
                  <p>From: {flight.from}</p>
                  <p>To: {flight.to}</p>
                  <p>Date: {flight.date}</p>
                  <p>Price: {flight.price} TL</p>
                  <p>Airline: {flight.airline}</p>
                  <Link to={`/seatselection/${flight.flightID}/${flight.from}/${flight.to}/${flight.price}`}>
                    <Button>Choose</Button>
                  </Link>
                  <hr />
                </div>
              ))
            ) : (
              <p>Ucuslari gormek icin arama yapin.</p>
            )
          )}
        </div>
      </Content>
      <Footer style={{ textAlign: 'center' }}>
        Ant Design ©{new Date().getFullYear()} Created by Ant UED
      </Footer>
    </Layout>
  );
};

export default MainPage;
