import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Button, Layout } from 'antd';
import './SeatSelection.css';

const { Header, Content, Footer } = Layout;

const SeatSelection = () => {
  const { flightID, from, to, price } = useParams();
  const [seats, setSeats] = useState([]);
  const [selectedSeat, setSelectedSeat] = useState(null);
  const [selectedSeatPrice, setSelectedSeatPrice] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch seat data from the API
    const fetchSeats = async () => {
      try {
        const response = await fetch(`https://v1.nocodeapi.com/emircan/google_sheets/TXdflyxmbKczXbgd?tabId=Sayfa2`);
        const data = await response.json();
        const filteredSeats = data.filter(seat => seat.flightID.toString() === flightID);
        setSeats(filteredSeats);
      } catch (error) {
        console.error('Error fetching seats:', error);
      }
    };

    fetchSeats();
  }, [flightID]);

  const handleSeatSelect = (seatNumber) => {
    const selected = seats.find(seat => seat.seats === seatNumber);
    if (selected && selected.availability === 'available') {
      setSelectedSeat(seatNumber);
      setSelectedSeatPrice(selected.price);
    }
  };

  const handleConfirmSelection = () => {
    if (selectedSeat) {
      // Mark selected seat as unavailable locally
      const updatedSeats = seats.map(seat => {
        if (seat.seats === selectedSeat) {
          return { ...seat, availability: 'unavailable' };
        }
        return seat;
      });
      setSeats(updatedSeats);

      // Prepare data for PUT request
      const putData = {
        row_id: seats.find(seat => seat.seats === selectedSeat).row_id,
        availability: 'unavailable',
      };

      // Prepare fetch request options
      const myHeaders = new Headers();
      myHeaders.append("Content-Type", "application/json");

      const requestOptions = {
        method: "PUT",
        headers: myHeaders,
        body: JSON.stringify(putData),
      };

      // Perform the fetch request to update the seat in Google Sheets API
      fetch("https://v1.nocodeapi.com/emircan/google_sheets/TXdflyxmbKczXbgd?tabId=Sayfa2", requestOptions)
        .then(response => response.text())
        .then(result => console.log(result)) // Handle successful response
        .catch(error => console.log('Error updating seat:', error)); // Handle error

      // Navigate to confirmation screen
      navigate(`/confirmationscreen/${flightID}/${selectedSeat}`, {
        state: {
          from,
          to,
          selectedSeat,
          selectedSeatPrice,
          price,
        },
      });
    } else {
      // If no seat is selected, handle as needed (random seat assignment logic)
      const availableSeats = seats.filter(seat => seat.availability === 'available');
      const randomSeat = availableSeats[Math.floor(Math.random() * availableSeats.length)];
      if (randomSeat) {
        setSelectedSeat(randomSeat.seats);
        setSelectedSeatPrice(randomSeat.price);

        // Navigate to confirmation screen with random seat
        navigate(`/confirmationscreen/${flightID}/${randomSeat.seats}`, {
          state: {
            from,
            to,
            selectedSeat: randomSeat.seats,
            selectedSeatPrice: randomSeat.price,
            price,
          },
        });
      }
    }
  };

  return (
    <Layout>
      <Header style={{ display: 'flex', alignItems: 'center' }}>
        <div className="demo-logo" />
        <h1 style={{ color: 'white', fontSize: '35px' }}>Yerinizi ayirin.</h1>
      </Header>
      <Content>
        <div>
          <h1>Secilen Ucus: {from} - {to}</h1>
          <div className="seat-grid">
            {seats.map((seat) => (
              <Button
                key={seat.seats}
                className={`seat-box ${seat.availability === 'available' ? 'available' : 'unavailable'} ${selectedSeat === seat.seats ? 'selected' : ''}`}
                onClick={() => handleSeatSelect(seat.seats)}
                disabled={seat.availability !== 'available'}
                title={seat.availability === 'available' ? `${seat.price} TL` : 'Unavailable'}
              >
                {seat.seats}
              </Button>
            ))}
          </div>
          {selectedSeat ? (
            <div>
              <h2>Secilen Koltuk: {selectedSeat}</h2>
              <h2>Fiyat: {selectedSeatPrice} TL</h2>
            </div>
          ) : (
            <h2>Rastgele yerlestirileceksiniz ve bu islem icin sizden ucret tahsil edilmeyecek</h2>
          )}
          <Button type="primary" onClick={handleConfirmSelection}>Confirm Selection</Button>
        </div>
      </Content>
      <Footer style={{ textAlign: 'center' }}>
        Ant Design Â©{new Date().getFullYear()} Created by Ant UED
      </Footer>
    </Layout>
  );
};

export default SeatSelection;
