import React from 'react';
import { Layout, Space, Input, InputNumber, Typography, theme } from 'antd';
import { useLocation } from 'react-router-dom';

const { Text } = Typography;
const { Header, Content, Footer } = Layout;

const ConfirmationScreen = () => {
  const {
    token: { colorBgContainer, borderRadiusLG },
  } = theme.useToken();

  const location = useLocation();
  const { from, to, selectedSeat } = location.state || {};

  return (
    <Layout>
      <Header style={{ display: 'flex', alignItems: 'center' }}>
        <div className="demo-logo" />
        <h1 style={{ color: 'white', fontSize: '35px' }}>Payment Confirmation</h1>
      </Header>
      <Content style={{ padding: '0 48px' }}>
        <div style={{ background: colorBgContainer, minHeight: 330, padding: 24, borderRadius: borderRadiusLG }}>
          <Space direction='vertical'>
            <Space style={{ marginBottom: '20px' }}>
              <Input placeholder='Cardholder Name' />
              <Input placeholder='Cardholder Surname' />
            </Space>
            <Space style={{ marginBottom: '20px' }}>
              <Input placeholder='Card Number' />
              <InputNumber placeholder='CVV' min={100} max={999} />
            </Space>
            <Space style={{ marginBottom: '20px' }}>
              <Text>Total Amount: $132.65</Text>
            </Space>
            <Space style={{ marginBottom: '20px' }}>
              <Text>1 Flight Ticket:</Text>
            </Space>
            <Space style={{ marginBottom: '20px' }}>
              {from && to && (
                <Text>From: {from} to {to}</Text>
              )}
            </Space>
            <Space style={{ marginBottom: '20px' }}>
              {selectedSeat && (
                <Text>Selected Seat: {selectedSeat}</Text>
              )}
            </Space>
          </Space>
        </div>
      </Content>
      <Footer style={{ textAlign: 'center' }}>
        Ant Design Â©{new Date().getFullYear()} Created by Ant UED
      </Footer>
    </Layout>
  );
};

export default ConfirmationScreen;
