import React from 'react';
import { useParams, useLocation, Link } from 'react-router-dom';
import { Layout, Space, DatePicker, Input, InputNumber, Typography, Button, Divider } from 'antd';

const { Text } = Typography;
const { Header, Content, Footer } = Layout;

const ConfirmationScreen = () => {
  const {selectedSeat } = useParams();
  const location = useLocation();
  const { from, to, selectedSeatPrice, price } = location.state || {};

  const ucusFiyat = parseFloat(price);
  const koltukFiyat = parseFloat(selectedSeatPrice);
  const toplamFiyat = ucusFiyat + koltukFiyat;

  return (
    <Layout>
      <Header style={{ display: 'flex', alignItems: 'center' }}>
        <div className="demo-logo" />
        <h1 style={{ color: 'white', fontSize: '35px' }}>Ödeme Ekranı</h1>
      </Header>
      <Content style={{ padding: '0 48px' }}>
        <div style={{ minHeight: 330, padding: 24 }}>
          <Space direction="horizontal" size="large">
            <Space direction="vertical">
              <Input placeholder="Cardholder Name" />
              <Input placeholder="Cardholder Surname" />
              <Input placeholder="Card Number" />
              <Space>
                <DatePicker.MonthPicker placeholder="MM/YYYY" />
                <InputNumber placeholder="CVV" min={100} max={999} />
                <Link to="/odendiekrani">
                  <Button type="primary">ÖDE</Button>
                </Link>
              </Space>
            </Space>
            <Space direction="vertical">
              <h1 style={{ fontSize: '30px' }}>Toplam: {toplamFiyat} TL</h1>
              <Divider plain>Hesap Özeti</Divider>
              <Text>1 Uçak Bileti: <Text strong>{ucusFiyat} TL</Text></Text>
              <Text>1 Koltuk Rezervasyonu: <Text strong>{koltukFiyat} TL</Text></Text>
              <Text>Nereden: <Text strong>{from}</Text> Nereye: <Text strong>{to}</Text></Text>
              <Text>Seçilen Koltuk Numarası: <Text strong>{selectedSeat}</Text></Text>
            </Space>
          </Space>
        </div>
      </Content>
      <Footer style={{ textAlign: 'center' }}>
        Ant Design ©{new Date().getFullYear()} Created by Ant UED
      </Footer>
    </Layout>
  );
};

export default ConfirmationScreen;
