import React, { useState } from 'react';
import { Layout, Space, DatePicker, theme, Input, InputNumber, Typography } from 'antd';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
import SizeContext from 'antd/es/config-provider/SizeContext';
import './ConfirmationScreen.css';
import Sider from 'antd/es/layout/Sider';
import 'antd/dist/reset.css';
const { Text } = Typography;

const { Header, Content, Footer } = Layout;

const ConfirmationScreen = () => {

  const { flightID, selectedSeat } = useParams();

  useEffect(() => {
    const updateSeatAvailability = async () => {
      var myHeaders = new Headers();
      myHeaders.append("Content-Type", "application/json");

      var requestOptions = {
        method: "PUT",
        headers: myHeaders,
        redirect: "follow",
        body: JSON.stringify({
          flightID: flightID,
          seat: selectedSeat,
          availability: "unavailable"
        })
      };

      try {
        const response = await fetch("https://v1.nocodeapi.com/emircankocatepe/google_sheets/JoFdgNIObUDXxQtO?tabId=Sayfa2", requestOptions);

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const result = await response.json();
        if (result.error) {
          throw new Error(`API error: ${result.info}`);
        }

        console.log("Seat availability updated successfully");
      } catch (error) {
        console.error('Error updating seat availability:', error);
      }
    };

    updateSeatAvailability();
  }, [flightID, selectedSeat]);


    const {
        token: { colorBgContainer, borderRadiusLG },
      } = theme.useToken();
      return (
        <Layout>

          <Header
            style={{
              display: 'flex',
              alignItems: 'center',
            }}
          >
            <div className="demo-logo" />
            <h1 style={{ color: 'white', fontSize: '35px' }}>Payment</h1>
          </Header>
          <Content
            style={{
              padding: '0 48px',

            }}
          >

            <div
              style={{
                background: colorBgContainer,
                minHeight: 330,
                padding: 24,
                borderRadius: borderRadiusLG,

              }}
            >
            <Space direction='horizontal' style={{gap:'240px'}}>
            <Space direction='vertical' style={{marginTop:'80px', marginLeft:'100px'}}>
                <Space direction='vertical' >
                    <Space>
                    <Input placeholder='Kart sahibinin ismi '></Input>
                    <Input placeholder='Kart sahibinin soyismi'></Input>
                    </Space>

                    <Input placeholder='Kart numarasini girin'></Input>

                </Space>
                <Space style={{marginLeft:'50px'}}>
                        <DatePicker.MonthPicker placeholder='YYYY/AA'></DatePicker.MonthPicker>

                        <InputNumber placeholder='CVV' min={100} max={999}></InputNumber>
                </Space>

            </Space>
            <Space direction='vertical' >
                <Space direction='vertical' >
                    <Space>
                    <h1>TOTAL: $132.65</h1>
                    <br/>
                    </Space>
                    <Text>1 Flight Ticket: </Text>
                </Space>

            </Space>
            </Space>  
            </div>
          </Content>

          <Footer
            style={{
              textAlign: 'center',
            }}
          >
            Ant Design ©{new Date().getFullYear()} Created by Ant UED
          </Footer>
        </Layout>
      );
    };
export default ConfirmationScreen
