import React, { useState } from 'react';
import { Layout, Space, Button, Checkbox, Select, DatePicker, theme, Text } from 'antd';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import MainPage from './Components/Pages/MainPage';
import ConfirmationScreen from './Components/Pages/ConfirmationScreen';
import Signup from './Components/Pages/Signup';
import Login from './Components/Pages/Login';
import SeatSelection from './Components/Pages/SeatSelection';
import GizliRota from './Components/Pages/DogrulamaYonlendirme';

const App = () => {

  const PrivateRoute = ({ auth: { isAuthenticated }, children }) => {
    return isAuthenticated ? children : <Navigate to="/login" />;
  };

  return (
    <BrowserRouter>
    <Routes>
      <Route path='/' element={<Login onLogin={() => setIsLoggedIn(true)} />} />
      <Route path='/signup' element={<Signup/>}></Route>

      <Route><PrivateRoute path="/mainpage" element={MainPage} isLoggedIn={isLoggedIn} /></Route>
        <PrivateRoute path="/seatselection" element={SeatSelection} isLoggedIn={isLoggedIn} />
        <PrivateRoute path="/confirmationscreen" element={ConfirmationScreen} isLoggedIn={isLoggedIn} />
      </Routes>
    </BrowserRouter>
  )
}


export default App;
